#include "LanguageRecognizer.h"

vector<string> stringSplitter(string input, string splitter) {
    vector<string> output;
    int slow = 0;
    char charSplitter = *splitter.c_str();
    for (int fast = 0; fast < input.size(); fast++) {
        if (input[fast] == charSplitter) {
            if (slow != 0) {
                output.push_back(input.substr(slow+1, fast - slow -1));
            } else {
                output.push_back(input.substr(slow, fast - slow -1));
            }
            slow = fast;
        }
    }
    output.push_back(input.substr(slow + 1, input.size() - slow - 1));
    return output;
}

